      whileHover={{ 
        y: -10,
        transition: { duration: 0.2 }
      }}
    >
      <Card className="group glass-card border-brand-aqua/20 hover:border-brand-aqua/40 transition-all duration-500 hover:shadow-xl h-full relative overflow-hidden">
