@tailwind base;
@tailwind components;
@tailwind utilities;

/* LIGHT MODE */
:root {
  --button-outline: rgba(0,0,0, .10);
  --badge-outline: rgba(0,0,0, .05);

  /* Automatic computation of border around primary / danger buttons */
  --opaque-button-border-intensity: -8; /* In terms of percentages */

  /* Backgrounds applied on top of other backgrounds when hovered/active */
  --elevate-1: rgba(0,0,0, .03);
  --elevate-2: rgba(0,0,0, .08);

  --background: 210 25% 98%;

  --foreground: 210 15% 18%;

  --border: 210 12% 88%;

  --card: 210 20% 96%;

  --card-foreground: 210 15% 18%;

  --card-border: 210 15% 92%;

  --sidebar: 210 18% 94%;

  --sidebar-foreground: 210 15% 18%;

  --sidebar-border: 210 18% 88%;

  --sidebar-primary: 194 44% 45%;

  --sidebar-primary-foreground: 210 25% 98%;

  --sidebar-accent: 210 15% 88%;

  --sidebar-accent-foreground: 210 15% 18%;

  --sidebar-ring: 194 44% 45%;

  --popover: 210 18% 92%;

  --popover-foreground: 210 15% 18%;

  --popover-border: 210 18% 86%;

  --primary: 197 100% 16%;

  --primary-foreground: 210 25% 98%;

  --secondary: 210 15% 86%;

  --secondary-foreground: 210 15% 18%;

  --muted: 210 12% 90%;

  --muted-foreground: 210 12% 35%;

  --accent: 194 20% 92%;

  --accent-foreground: 210 15% 18%;

  --destructive: 0 84% 35%;

  --destructive-foreground: 0 0% 98%;

  --input: 210 15% 78%;
  --ring: 194 44% 45%;
  --chart-1: 197 100% 16%;
  --chart-2: 194 44% 45%;
  --chart-3: 48 100% 16%;
  --chart-4: 194 75% 30%;
  --chart-5: 210 15% 25%;

  --font-sans: Inter, sans-serif;
  --font-serif: Georgia, serif;
  --font-mono: Menlo, monospace;
  --radius: .5rem; /* 8px */

  /* Motion Tokens */
  --duration-fast: 200ms;
  --duration-normal: 300ms;
  --duration-slow: 400ms;
  --ease-standard: cubic-bezier(0.25, 0.46, 0.45, 0.94);
  --ease-entrance: cubic-bezier(0.25, 0.46, 0.45, 0.94);
  --ease-emphasis: cubic-bezier(0.68, -0.55, 0.265, 1.55);

  /* Enhanced Shadows */
  --shadow-2xs: 0px 2px 0px 0px hsl(210 15% 18% / 0.03);
  --shadow-xs: 0px 2px 0px 0px hsl(210 15% 18% / 0.05);
  --shadow-sm: 0px 2px 0px 0px hsl(210 15% 18% / 0.05), 0px 1px 2px -1px hsl(210 15% 18% / 0.08);
  --shadow: 0px 2px 0px 0px hsl(210 15% 18% / 0.08), 0px 1px 2px -1px hsl(210 15% 18% / 0.12);
  --shadow-md: 0px 2px 0px 0px hsl(210 15% 18% / 0.10), 0px 2px 4px -1px hsl(210 15% 18% / 0.15);
  --shadow-lg: 0px 2px 0px 0px hsl(210 15% 18% / 0.12), 0px 4px 6px -1px hsl(210 15% 18% / 0.18);
  --shadow-xl: 0px 2px 0px 0px hsl(210 15% 18% / 0.15), 0px 8px 10px -1px hsl(210 15% 18% / 0.22);
  --shadow-2xl: 0px 2px 0px 0px hsl(210 15% 18% / 0.25);
  --shadow-glow: 0 0 20px hsl(194 44% 45% / 0.3);
  --shadow-float: 0 20px 25px -5px hsl(210 15% 18% / 0.1), 0 10px 10px -5px hsl(210 15% 18% / 0.04);

  /* Glassmorphism */
  --glass-bg: rgba(248, 249, 250, 0.8);
  --glass-border: rgba(64, 143, 164, 0.2);
  --glass-blur: 16px;

  /* Brand Gradients */
  --gradient-primary: linear-gradient(135deg, #003752 0%, #408FA4 100%);
  --gradient-secondary: linear-gradient(135deg, #408FA4 0%, #6BA8B8 100%);
  --gradient-mesh: radial-gradient(circle at 20% 80%, rgba(64, 143, 164, 0.2) 0%, transparent 50%), 
                   radial-gradient(circle at 80% 20%, rgba(0, 55, 82, 0.2) 0%, transparent 50%),
                   radial-gradient(circle at 40% 40%, rgba(64, 143, 164, 0.1) 0%, transparent 50%);

  --tracking-normal: 0em;
  --spacing: 0.25rem;

  /* Automatically computed borders - intensity can be controlled by the user by the --opaque-button-border-intensity setting */

  /* Fallback for older browsers */
  --sidebar-primary-border: hsl(var(--sidebar-primary));
  --sidebar-primary-border: hsl(from hsl(var(--sidebar-primary)) h s calc(l + var(--opaque-button-border-intensity)) / alpha);

  /* Fallback for older browsers */
  --sidebar-accent-border: hsl(var(--sidebar-accent));
  --sidebar-accent-border: hsl(from hsl(var(--sidebar-accent)) h s calc(l + var(--opaque-button-border-intensity)) / alpha);

  /* Fallback for older browsers */
  --primary-border: hsl(var(--primary));
  --primary-border: hsl(from hsl(var(--primary)) h s calc(l + var(--opaque-button-border-intensity)) / alpha);

  /* Fallback for older browsers */
  --secondary-border: hsl(var(--secondary));
  --secondary-border: hsl(from hsl(var(--secondary)) h s calc(l + var(--opaque-button-border-intensity)) / alpha);

  /* Fallback for older browsers */
  --muted-border: hsl(var(--muted));
  --muted-border: hsl(from hsl(var(--muted)) h s calc(l + var(--opaque-button-border-intensity)) / alpha);

  /* Fallback for older browsers */
  --accent-border: hsl(var(--accent));
  --accent-border: hsl(from hsl(var(--accent)) h s calc(l + var(--opaque-button-border-intensity)) / alpha);

  /* Fallback for older browsers */
  --destructive-border: hsl(var(--destructive));
  --destructive-border: hsl(from hsl(var(--destructive)) h s calc(l + var(--opaque-button-border-intensity)) / alpha);
}

.dark {
  --button-outline: rgba(255,255,255, .10);
  --badge-outline: rgba(255,255,255, .05);

  --opaque-button-border-intensity: 9;  /* In terms of percentages */

  /* Backgrounds applied on top of other backgrounds when hovered/active */
  --elevate-1: rgba(255,255,255, .04);
  --elevate-2: rgba(255,255,255, .09);

  --background: 210 6% 8%;

  --foreground: 210 15% 92%;

  --border: 210 8% 14%;

  --card: 210 6% 10%;

  --card-foreground: 210 15% 92%;

  --card-border: 210 8% 16%;

  --sidebar: 210 6% 12%;

  --sidebar-foreground: 210 15% 92%;

  --sidebar-border: 210 8% 18%;

  --sidebar-primary: 194 44% 45%;

  --sidebar-primary-foreground: 210 25% 98%;

  --sidebar-accent: 210 8% 18%;

  --sidebar-accent-foreground: 210 15% 92%;

  --sidebar-ring: 194 44% 45%;

  --popover: 210 6% 14%;

  --popover-foreground: 210 15% 92%;

  --popover-border: 210 8% 20%;

  --primary: 197 100% 16%;

  --primary-foreground: 210 25% 98%;

  --secondary: 210 8% 20%;

  --secondary-foreground: 210 15% 92%;

  --muted: 210 6% 16%;

  --muted-foreground: 210 12% 75%;

  --accent: 194 15% 18%;

  --accent-foreground: 210 15% 92%;

  --destructive: 0 84% 35%;

  --destructive-foreground: 0 0% 98%;

  --input: 210 8% 25%;
  --ring: 194 44% 45%;
  --chart-1: 197 100% 65%;
  --chart-2: 194 60% 70%;
  --chart-3: 48 100% 60%;
  --chart-4: 194 75% 60%;
  --chart-5: 210 15% 75%;

  --shadow-2xs: 0px 2px 0px 0px hsl(210 6% 2% / 0.30);
  --shadow-xs: 0px 2px 0px 0px hsl(210 6% 2% / 0.35);
  --shadow-sm: 0px 2px 0px 0px hsl(210 6% 2% / 0.35), 0px 1px 2px -1px hsl(210 6% 2% / 0.40);
  --shadow: 0px 2px 0px 0px hsl(210 6% 2% / 0.40), 0px 1px 2px -1px hsl(210 6% 2% / 0.45);
  --shadow-md: 0px 2px 0px 0px hsl(210 6% 2% / 0.45), 0px 2px 4px -1px hsl(210 6% 2% / 0.50);
  --shadow-lg: 0px 2px 0px 0px hsl(210 6% 2% / 0.50), 0px 4px 6px -1px hsl(210 6% 2% / 0.55);
  --shadow-xl: 0px 2px 0px 0px hsl(210 6% 2% / 0.55), 0px 8px 10px -1px hsl(210 6% 2% / 0.60);
  --shadow-2xl: 0px 2px 0px 0px hsl(210 6% 2% / 0.70);

}

@layer base {
  * {
    @apply border-border;
  }

  body {
    @apply font-sans antialiased bg-background text-foreground;
  }
}

/**
 * Using the elevate system.
 * Automatic contrast adjustment.
 *
 * <element className="hover-elevate" />
 * <element className="active-elevate-2" />
 *
 * // Using the tailwind utility when a data attribute is "on"
 * <element className="toggle-elevate data-[state=on]:toggle-elevated" />
 * // Or manually controlling the toggle state
 * <element className="toggle-elevate toggle-elevated" />
 *
 * Elevation systems have to handle many states.
 * - not-hovered, vs. hovered vs. active  (three mutually exclusive states)
 * - toggled or not
 * - focused or not (this is not handled with these utilities)
 *
 * Even without handling focused or not, this is six possible combinations that
 * need to be distinguished from eachother visually.
 */
@layer utilities {

  /* Hide ugly search cancel button in Chrome until we can style it properly */
  input[type="search"]::-webkit-search-cancel-button {
    @apply hidden;
  }

  /* Placeholder styling for contentEditable div */
  [contenteditable][data-placeholder]:empty::before {
    content: attr(data-placeholder);
    color: hsl(var(--muted-foreground));
    pointer-events: none;
  }

  /* .no-default-hover-elevate/no-default-active-elevate is an escape hatch so consumers of
   * buttons/badges can remove the automatic brightness adjustment on interactions
   * and program their own. */
  .no-default-hover-elevate {}

  .no-default-active-elevate {}


  /**
   * Toggleable backgrounds go behind the content. Hoverable/active goes on top.
   * This way they can stack/compound. Both will overlap the parent's borders!
   * So borders will be automatically adjusted both on toggle, and hover/active,
   * and they will be compounded.
   */
  .toggle-elevate::before,
  .toggle-elevate-2::before {
    content: "";
    pointer-events: none;
    position: absolute;
    inset: 0px;
    /*border-radius: inherit;   match rounded corners */
    border-radius: inherit;
    z-index: -1;
    /* sits behind content but above backdrop */
  }

  .toggle-elevate.toggle-elevated::before {
    background-color: var(--elevate-2);
  }

  /* If there's a 1px border, adjust the inset so that it covers that parent's border */
  .border.toggle-elevate::before {
    inset: -1px;
  }

  /* Does not work on elements with overflow:hidden! */
  .hover-elevate:not(.no-default-hover-elevate),
  .active-elevate:not(.no-default-active-elevate),
  .hover-elevate-2:not(.no-default-hover-elevate),
  .active-elevate-2:not(.no-default-active-elevate) {
    position: relative;
    z-index: 0;
  }

  .hover-elevate:not(.no-default-hover-elevate)::after,
  .active-elevate:not(.no-default-active-elevate)::after,
  .hover-elevate-2:not(.no-default-hover-elevate)::after,
  .active-elevate-2:not(.no-default-active-elevate)::after {
    content: "";
    pointer-events: none;
    position: absolute;
    inset: 0px;
    /*border-radius: inherit;   match rounded corners */
    border-radius: inherit;
    z-index: 999;
    /* sits in front of content */
  }

  .hover-elevate:hover:not(.no-default-hover-elevate)::after,
  .active-elevate:active:not(.no-default-active-elevate)::after {
    background-color: var(--elevate-1);
  }

  .hover-elevate-2:hover:not(.no-default-hover-elevate)::after,
  .active-elevate-2:active:not(.no-default-active-elevate)::after {
    background-color: var(--elevate-2);
  }

  /* If there's a 1px border, adjust the inset so that it covers that parent's border */
  .border.hover-elevate:not(.no-hover-interaction-elevate)::after,
  .border.active-elevate:not(.no-active-interaction-elevate)::after,
  .border.hover-elevate-2:not(.no-hover-interaction-elevate)::after,
  .border.active-elevate-2:not(.no-active-interaction-elevate)::after,
  .border.hover-elevate:not(.no-hover-interaction-elevate)::after {
    inset: -1px;
  }

  /* Advanced Animation Utilities */
  .fade-in {
    opacity: 0;
    animation: fadeIn var(--duration-slow) var(--ease-entrance) forwards;
  }

  .fade-up {
    opacity: 0;
    transform: translateY(20px);
    animation: fadeUp var(--duration-slow) var(--ease-entrance) forwards;
  }

  .scale-in {
    opacity: 0;
    transform: scale(0.95);
    animation: scaleIn var(--duration-normal) var(--ease-entrance) forwards;
  }

  .slide-in-left {
    opacity: 0;
    transform: translateX(-30px);
    animation: slideInLeft var(--duration-slow) var(--ease-entrance) forwards;
  }

  .slide-in-right {
    opacity: 0;
    transform: translateX(30px);
    animation: slideInRight var(--duration-slow) var(--ease-entrance) forwards;
  }

  .float {
    animation: float 6s ease-in-out infinite;
  }

  .shimmer {
    background: linear-gradient(90deg, transparent, rgba(255,255,255,0.2), transparent);
    background-size: 200% 100%;
    animation: shimmer 2s infinite;
  }

  .draw-line {
    stroke-dasharray: 1000;
    stroke-dashoffset: 1000;
    animation: drawLine 3s var(--ease-standard) forwards;
  }

  .glass-card {
    background: var(--glass-bg);
    backdrop-filter: blur(var(--glass-blur));
    border: 1px solid var(--glass-border);
  }

  .gradient-border {
    position: relative;
    background: var(--gradient-primary);
    padding: 1px;
    border-radius: 0.75rem;
  }

  .gradient-border::before {
    content: '';
    position: absolute;
    inset: 0;
    padding: 1px;
    background: var(--gradient-primary);
    border-radius: inherit;
    mask: linear-gradient(#fff 0 0) content-box, linear-gradient(#fff 0 0);
    mask-composite: exclude;
  }

  /* Respect user preferences */
  @media (prefers-reduced-motion: reduce) {
    .fade-in,
    .fade-up,
    .scale-in,
    .slide-in-left,
    .slide-in-right {
      animation: none !important;
      opacity: 1 !important;
      transform: none !important;
    }

    .float,
    .shimmer,
    .draw-line {
      animation: none !important;
    }
  }

/* Keyframes */
@keyframes fadeIn {
  to {
    opacity: 1;
  }
}

@keyframes fadeUp {
  to {
    opacity: 1;
    transform: translateY(0);
  }
}

@keyframes scaleIn {
  to {
    opacity: 1;
    transform: scale(1);
  }
}

@keyframes slideInLeft {
  to {
    opacity: 1;
    transform: translateX(0);
  }
}

@keyframes slideInRight {
  to {
    opacity: 1;
    transform: translateX(0);
  }
}

@keyframes float {
  0%, 100% {
    transform: translateY(0px);
  }
  50% {
    transform: translateY(-10px);
  }
}

@keyframes shimmer {
  0% {
    background-position: -200% 0;
  }
  100% {
    background-position: 200% 0;
  }
}

@keyframes drawLine {
  to {
    stroke-dashoffset: 0;
  }
}

/* Responsive Typography */
.fluid-text-xs { font-size: clamp(0.75rem, 0.7rem + 0.25vw, 0.875rem); }
.fluid-text-sm { font-size: clamp(0.875rem, 0.8rem + 0.375vw, 1rem); }
.fluid-text-base { font-size: clamp(1rem, 0.9rem + 0.5vw, 1.125rem); }
.fluid-text-lg { font-size: clamp(1.125rem, 1rem + 0.625vw, 1.25rem); }
.fluid-text-xl { font-size: clamp(1.25rem, 1.1rem + 0.75vw, 1.5rem); }
.fluid-text-2xl { font-size: clamp(1.5rem, 1.3rem + 1vw, 2rem); }
.fluid-text-3xl { font-size: clamp(1.875rem, 1.5rem + 1.875vw, 3rem); }
.fluid-text-4xl { font-size: clamp(2.25rem, 1.8rem + 2.25vw, 3.75rem); }
.fluid-text-5xl { font-size: clamp(3rem, 2.25rem + 3.75vw, 4.5rem); }
.fluid-text-6xl { font-size: clamp(3.75rem, 2.7rem + 5.25vw, 6rem); }
}

