          animate={inView ? { opacity: 1, y: 0 } : { opacity: 0, y: 30 }}
          transition={{ duration: 0.6, delay: 0.8 }}
        >
          <p className="text-muted-foreground fluid-text-lg">
            Join hundreds of professionals who've transformed their careers
          </p>
        </motion.div>
      </div>
    </section>
  );
}

